import{V as t}from"./chunks/theme.6950fb3c.js";import{o as r,c as l,L as o,l as i,k as e,a as s,V as n}from"./chunks/framework.b2940161.js";const c=e("h1",{id:"关于作者",tabindex:"-1"},[s("关于作者 "),e("a",{class:"header-anchor",href:"#关于作者","aria-label":'Permalink to "关于作者"'},"​")],-1),h=n('<h2 id="作者简介" tabindex="-1">作者简介 <a class="header-anchor" href="#作者简介" aria-label="Permalink to &quot;作者简介&quot;">​</a></h2><p>🚀码农一枚，产于上世纪80年代，拥有20+年上网经历，👨‍💻擅长 Linux/Mac/Windows 系统开关机，精通 Go/JS/Rust/Python/C 语言世界的 Hello World 编写。🎩</p><p>📚除了写代码，我还是个讲师，有着“中移联合智库ICT专家级讲师”的称号。一汽大众等国企的内训课程，以及微信学堂<a href="https://developers.weixin.qq.com/community/business/course/000606628dc2e86dc0ddcbb115940d" target="_blank" rel="noreferrer">《小程序性能优化实践》</a>和极客时间<a href="http://gk.link/a/10AdC" target="_blank" rel="noreferrer">《微信小程序全栈开发实战》</a>，都有我传授知识的身影和声音。🎙️</p><p>🖊️白天我是一名计算机讲师兼作家，出版过<a href="https://item.jd.com/10070363837259.html" target="_blank" rel="noreferrer">《微信小游戏开发》</a>、<a href="https://item.jd.com/27148951176.html" target="_blank" rel="noreferrer">《小程序从0到1：微信全栈工程师一本通》</a>等图书，获得了<a href="https://cloud.tencent.com/tvp/124" target="_blank" rel="noreferrer">腾讯云最具价值专家（TVP）</a>荣誉称号，也被腾讯课堂选为年度最具影响力专家。🏆</p><p>📖在夜深人静的时候，我化身为一名网文小说家，书写着天马行空的虚拟故事。🌙忙碌之余，我喜欢散步，🎵享受箫等传统乐器。欢迎关注我，与时间做朋友一同成长，一起探索这个充满魅力的技术世界。</p><p>我的公众号/视频号如下：</p><img src="https://yishulun.com/yslqrcode.jpg" style="zoom:50%;"><h2 id="联系方式" tabindex="-1">联系方式 <a class="header-anchor" href="#联系方式" aria-label="Permalink to &quot;联系方式&quot;">​</a></h2><ul><li>Email：<code>9830131#qq.com</code></li><li>Weixin/QQ：9830131</li></ul><p>添加微信，回复“读者”，可以进读者修仙群。</p><h2 id="社区帐号" tabindex="-1">社区帐号 <a class="header-anchor" href="#社区帐号" aria-label="Permalink to &quot;社区帐号&quot;">​</a></h2><ul><li><a href="https://juejin.cn/user/2400989124504286" target="_blank" rel="noreferrer">掘金</a></li><li><a href="https://www.zhihu.com/people/liyi2005" target="_blank" rel="noreferrer">知乎</a></li><li><a href="https://weibo.com/u/2820420060" target="_blank" rel="noreferrer">微博</a></li><li><a href="https://twitter.com/coderliyi" target="_blank" rel="noreferrer">推特</a></li></ul>',12),b=JSON.parse('{"title":"关于作者","description":"","frontmatter":{"editLink":false,"comment":false,"pageInfo":false,"date":"2002-12-22T10:50:22.000Z","footer":false,"sidebar":false,"toc":false},"headers":[],"relativePath":"about.md","filePath":"about.md","lastUpdated":1691758534000}'),d={name:"about.md"},k=Object.assign(d,{setup(m){const a=[{avatar:"https://yishulun.com/avatar.png",name:"LIYI",title:"计算机讲师、作家，网文小说作者。白天写代码，晚上写梦想。",links:[{icon:"github",link:"https://github.com/rixingyike"},{icon:"twitter",link:"https://twitter.com/coderliyi"}]}];return(p,f)=>(r(),l("div",null,[c,o(i(t),{size:"small",members:a}),h]))}});export{b as __pageData,k as default};
