import{_ as t,o as a,c as s,k as e,a as o}from"./chunks/framework.25d5caa7.js";const x=JSON.parse('{"title":"关于价值的判定","description":"","frontmatter":{"sidebar":false,"date":"2021-03-28T00:00:00.000Z"},"headers":[],"relativePath":"posts/2021/3.md","filePath":"posts/2021/3.md"}'),r={name:"posts/2021/3.md"},n=e("h1",{id:"关于价值的判定",tabindex:"-1"},[o("关于价值的判定 "),e("a",{class:"header-anchor",href:"#关于价值的判定","aria-label":'Permalink to "关于价值的判定"'},"​")],-1),c=e("p",null,"我们是如何判定一件事的价值的呢？靠知识与逻辑经验？靠经验？还是靠直觉？如果没有经验，又不具备相关的知识，一般我们是很难判定一件事的价值的。所以一般人很难突破自己，主要是因为不具备价值界定与判断的能力。试想一下，如果将时光倒回10年，你会做什么事？ 有人说，我会在北京回龙观买房。但是当时，你可能也知道这个房源信息，当时没有行动，主要原因在于你当时无法界定这件事的价值。 有的人靠直觉，即使他没有相关经验，也无法从知识与逻辑上证明某件事能成，但是就坚定不移地做了。有时候这种直觉又被称为商业嗅觉。",-1),d=[n,c];function i(_,l,p,h,f,m){return a(),s("div",null,d)}const k=t(r,[["render",i]]);export{x as __pageData,k as default};
