import{_ as t,o as a,c as s,k as e,a as o}from"./chunks/framework.b2940161.js";const b=JSON.parse('{"title":"代码模板与武侠小说的情节套路","description":"","frontmatter":{"sidebar":false,"date":"2022-10-04T00:00:00.000Z","category":["文学"],"tag":["小说"]},"headers":[],"relativePath":"posts/2022/1004.md","filePath":"posts/2022/1004.md","lastUpdated":1691329720000}'),n={name:"posts/2022/1004.md"},r=e("h1",{id:"代码模板与武侠小说的情节套路",tabindex:"-1"},[o("代码模板与武侠小说的情节套路 "),e("a",{class:"header-anchor",href:"#代码模板与武侠小说的情节套路","aria-label":'Permalink to "代码模板与武侠小说的情节套路"'},"​")],-1),c=e("p",null,"代码模板是一种可以填空或在其基础上改写的代码范式，可以在一定程度上帮助程序员加快写代码的速度，减少重复枯燥的键盘操作。在小说中也有类似的情节套路与之对应。",-1),d=e("p",null,"金庸先生自称在写每一部小说时，都要力求有些变化，要故意写得不一样，但这并不是说金庸的小说没有重复。相反，金庸先生这样想反方向用力，恰恰说明是存在重复的，且这种重复很容易发生的。",-1),_=e("p",null,"下面列两个例子。在「神雕侠侣」中，郭襄将要过生日，突然有百草仙等陌生人向她送很多珍贵的贺礼，这些礼物让郭靖和黄蓉都感到吃惊，以他们二人都未必有这么大的面子；对比在「笑傲江湖」中，令狐冲受伤，随师门同行，不断有陌生人主动上前送很多珍贵的药材和礼物，连岳不群夫妇都觉得没有这样大的面子。在「射雕英雄传」中，黄蓉初识洪七公，想甩下七公，却不料每一次反而让七公跑到了她的前面；在「神雕侠侣」中，郭襄想甩下金轮法王逃走，每次都发现金轮法王早已经跑到了她的前面。相信类似的套路还有很多。",-1),l=e("p",null,"类似的武侠小说中这样的情节套路，是可以像代码模板那样复用的。代码模板在使用时，关键信息是有修改的，不是全部代码都一样；情节套路在复用时，也是与角色、当下故事情节契合的，也不是完全照搬，所以读者也不会感到明显的重复。",-1),i=[r,c,d,_,l];function p(h,f,m,u,x,k){return a(),s("div",null,i)}const g=t(n,[["render",p]]);export{b as __pageData,g as default};
