import{_ as e,o as t,c as a,Q as r}from"./chunks/framework.25d5caa7.js";const o="/assets/Lex对马斯克的全面采访-封面.c912ad80.jpg",u=JSON.parse('{"title":"Lex对马斯克的全面采访","description":"","frontmatter":{"date":"2023-11-10T20:58:51.000Z"},"headers":[],"relativePath":"posts/2023/37.md","filePath":"posts/2023/37.md"}'),s={name:"posts/2023/37.md"},p=r('<h1 id="lex对马斯克的全面采访" tabindex="-1">Lex对马斯克的全面采访 <a class="header-anchor" href="#lex对马斯克的全面采访" aria-label="Permalink to &quot;Lex对马斯克的全面采访&quot;">​</a></h1><p><img src="'+o+'" alt="Lex对马斯克的全面采访-封面"></p><p><a href="https://youtu.be/l1Cf8iq6k1w" target="_blank" rel="noreferrer">https://youtu.be/l1Cf8iq6k1w</a></p><p>这是 <a href="https://twitter.com/lexfridman/status/1722686021781835928" target="_blank" rel="noreferrer">Lex Fridman</a> 对马斯克做的第 4 次采访，也是它博客上第 400 个采访，涉及内容十分广泛，时间目录如下所示。</p><p>0:00 - 简介 0:07 - 战争与人性 4:33 - 以色列-哈马斯战争 10:41 - 军工综合体 14:58 - 乌克兰战争 19:41 - 中国 33:57 - xAI 格罗克（Grok） 44:55 - 外星人 52:55 - 神 55:22 - 暗黑破坏神 4 和视频游戏 1:04:29 - 反乌托邦世界：1984 年和美丽新世界 1:10:41 - 人工智能和每瓦有用的计算 1:16:22 - 人工智能监管 1:23:14 - 人工智能应该开源吗？ 1:30:36 - X 算法 1:41:57 - 2024 年总统选举 1:54:55 - 政治 1:57:57 - 信任 2:03:29 - 特斯拉的自动驾驶仪和 Optimus 机器人 2:12:28 - 艰辛</p><p>原视频是英文的，我使用工具加上中文字幕，方便国内读者观看。个别地方自动翻译不准确，需自行甄别纠正。</p><h2 id="中国威胁问题" tabindex="-1">中国威胁问题 <a class="header-anchor" href="#中国威胁问题" aria-label="Permalink to &quot;中国威胁问题&quot;">​</a></h2><p>在论到中国在成为世界强国后会不会对世界产生威胁，马斯克表示，不会。中国是一个历史悠久的国家，在三国时期曾有过十分激烈的内战，人口几乎损失了七成，这是非常糟糕的。但中国在历史上没有侵略过其它国家，中国像一个集合体，有很强的包容性，但没有主动侵略过其他国家。所以，马斯克认为中国的强大对于世界而言，是安全的。</p><h2 id="外星人问题" tabindex="-1">外星人问题 <a class="header-anchor" href="#外星人问题" aria-label="Permalink to &quot;外星人问题&quot;">​</a></h2><p>没有。马斯克表示，如果 Spacex 发现外星人的踪迹，将在 X 上第一时间发布。</p>',10),n=[p];function i(_,l,c,d,h,f){return t(),a("div",null,n)}const x=e(s,[["render",i]]);export{u as __pageData,x as default};
